/**
 * Computes direction vector `a` -> `b`, normalized to length `n`
 * (default 1).
 *
 * @param a -
 * @param b -
 * @param n -
 */
const direction = (out, a, b, n = 1) => normalize(null, sub(out || a, b, a), n);

export { direction };
