import { defColor } from "../defcolor.js";
import { hsvHsl } from "../hsv/hsv-hsl.js";
import { rgbHsl } from "../rgb/rgb-hsl.js";
import { hslRgb } from "./hsl-rgb.js";
export const hsl = defColor({
    mode: "hsl",
    order: ["h", "s", "l", "alpha"],
    from: { rgb: rgbHsl, srgb: rgbHsl, hsv: hsvHsl },
    toRgb: hslRgb,
});
