import { cossin } from "@thi.ng/math/angle";
import { fit01 } from "@thi.ng/math/fit";
import { add2 } from "@thi.ng/vectors/add";
import { mul2 } from "@thi.ng/vectors/mul";
import { rotateZ } from "@thi.ng/vectors/rotate";
export const pointAt = (pos, r, axis, start, end, t, out = []) => pointAtTheta(pos, r, axis, fit01(t, start, end), out);
export const pointAtTheta = (pos, r, axis, theta, out = []) => add2(null, rotateZ(null, mul2(out, cossin(theta), r), axis), pos);
