import { DEFAULT, defmulti } from "@thi.ng/defmulti/defmulti";
import { neg } from "@thi.ng/vectors/neg";
import { __dispatch } from "./internal/dispatch.js";
export const flip = defmulti(__dispatch, {
    cubic: "points",
    line: "points",
    points3: "points",
    poly: "points",
    polyline: "points",
    quad: "points",
    quadratic: "points",
    tri: "points",
}, {
    [DEFAULT]: (x) => x,
    arc: ($) => {
        const t = $.start;
        $.start = $.end;
        $.end = t;
        $.cw = !$.cw;
        return $;
    },
    group: ($) => {
        $.children.forEach(flip);
        return $;
    },
    path: ($) => {
        // TODO
        return $;
    },
    points: ($) => {
        $.points.reverse();
        return $;
    },
    ray: ($) => {
        $.dir = neg(null, $.dir);
        return $;
    },
});
